shader.code:

in vec3 pos;
in vec2 uv;

out vec2 UV;
void main()
{
  gl_Position = vec4(pos, 1.0f);
  UV = uv;
}


in vec2 UV;
uniform sampler2D tex;
out vec4 FragColor0;
void main()
{
  FragColor0 = texture(tex, UV);
}


in vec3 pos;
in vec2 uv;

uniform vec2 twoTexelSize;

out vec2 UV1;
out vec2 UV2;
out vec2 UV3;
out vec2 UV4;

void main()
{
  gl_Position = vec4(pos, 1.0f);
  UV1 = uv;
  UV2 = uv + vec2(twoTexelSize.x, 0);
  UV3 = uv + vec2(twoTexelSize.x, twoTexelSize.y);
  UV4 = uv + vec2(0, twoTexelSize.y);
}


in vec2 UV1;
in vec2 UV2;
in vec2 UV3;
in vec2 UV4;
uniform sampler2D tex;
out vec4 FragColor0;
void main()
{
  FragColor0 = (texture(tex, UV1) + 
                texture(tex, UV2) +
                texture(tex, UV3) +
                texture(tex, UV4)) * 0.25;
}
