shader.code:

in float p;

uniform mat4 mvp;
uniform vec3 v0;
uniform vec3 v1;
uniform vec3 v2;
uniform vec3 v3;
uniform vec3 v4;
uniform vec3 v5;
uniform vec3 v6;
uniform vec3 v7;

void main()
{
	vec3 pos;
	if (p < 0.5f)
	{
		pos = v0;
	}
	else if (p < 1.5f)
	{
		pos = v1;
	}
	else if (p < 2.5f)
	{
		pos = v2;
	}
	else if (p < 3.5f)
	{
		pos = v3;
	}
	else if (p < 4.5f)
	{
		pos = v4;
	}
	else if (p < 5.5f)
	{
		pos = v5;
	}
	else if (p < 6.5f)
	{
		pos = v6;
	}
	else
	{
		pos = v7;
	}
	gl_Position = mvp * vec4(pos, 1.0f);
}


vec4 cccc(mat4 mvp, vec3 pos)
{
  return mvp * vec4(pos, 1.0f);
}


in vec3 pos;

uniform mat4 mvp;

void main()
{
  gl_Position = cccc(mvp, pos);
	//gl_Position = mvp * vec4(pos, 1.0f);
}


out vec4 out_Color;

uniform vec4 color;

void main()
{
  out_Color = color;
}


in vec3 pos;
in vec2 texCoord;

uniform mat4 mvp;

out vec2 TexCoord;

void main()
{
	gl_Position = mvp * vec4(pos, 1.0f);
  TexCoord = texCoord;
}


in vec2 TexCoord;

uniform sampler2D albedo_tex;

out vec4 Color;

void main()
{
  Color = texture(albedo_tex, TexCoord);
  Color.a = 1.0f;
}
