shader.code:

in vec3 pos;
in vec3 normal;

uniform mat4 mvp;

out float Diffuse;

const vec3 light_dir = vec3(1.0, 1.0, 1.0);
void main()
{
  gl_Position = mvp * vec4(pos, 1.0f);
  Diffuse = max(dot(normal, light_dir), 0.25f);
}


in float Diffuse;

uniform vec4 color;

out vec4 FragColor;

void main()
{
  FragColor = color * Diffuse;
}
